import http from "./httpService";
import axios from 'axios';

export async function getBooks() {

  const { data: books, cancel } = await http.get("books", {
    cancelToken: new axios.CancelToken(function executor(c){
      cancel = c;
    })
  });
  return books, cancel;
}
