import React from "react";

const Input = (name, label, type, item, setItem) => {
  return (
    <div className="form-group">
      <label htmlFor={`input-${name}`}>{label}</label>
      <input
        id={`input-${name}`}
        type={type}
        className="form-control"
        value={item[name]}
        onChange={e =>
          setItem(prev => ({ ...prev, [name]: e.currentTarget.value }))
        }
      />
    </div>
  );
};

export default Input;
