import React, { useState } from "react";
import Joi from "joi-browser";
import { Redirect } from "react-router-dom";

import Input from "./common/input";
import { login, getCurrentUser } from "../services/authService";

const LoginForm = ({ location }) => {
  const [user, setUser] = useState({ username: "", email: "", password: "" });
  const [errors, setErrors] = useState({});

  if (getCurrentUser()) return <Redirect to="/" />;

  const schema = {
    username: Joi.string()
      .required()
      .label("Username"),
    email: Joi.string().allow(""),
    password: Joi.string()
      .required()
      .label("Password")
  };

  const inputList = [
    {
      name: "username",
      label: "Username",
      value: user.username,
      element: "Input"
    },
    {
      name: "password",
      label: "Password",
      value: user.password,
      element: "Input"
    },
    { label: "Login", element: "button" }
  ];

  function validate() {
    const options = { abortEarly: false };
    const { error } = Joi.validate(user, schema, options);
    if (!error) return null;

    const errors = {};
    for (let item of error.details) errors[item.path[0]] = item.message;
    return errors;
  }

  function validateProperty({ name, value }) {
    const obj = { [name]: value };
    const propertySchema = { [name]: schema[name] };
    const { error } = Joi.validate(obj, propertySchema);

    return error ? error.details[0].message : null;
  }

  function handleChange({ currentTarget: input }) {
    const allErrors = errors;
    const errorMessage = validateProperty(input);
    if (errorMessage) allErrors[input.name] = errorMessage;
    else delete allErrors[input.name];

    setErrors(allErrors);
    setUser({ ...user, [input.name]: input.value });
  }

  function handleSubmit(e) {
    e.preventDefault();

    const errors = validate();
    setErrors(errors || {});
    if (errors) return;

    doSubmit();
  }

  async function doSubmit() {
    try {
      await login(user);
      const { state } = location;
      window.location = state ? state.from.pathname : "/";
    } catch (ex) {
      if (
        ex.response &&
        ex.response.status >= 400 &&
        ex.response.status <= 500
      ) {
        setErrors({ ...errors, username: ex.response.data.detail });
      }
    }
  }

  function renderButton(label) {
    return (
      <button type="submit" className="btn btn-primary" disabled={validate()}>
        {label}
      </button>
    );
  }

  function renderInput(name, label, type = "text") {
    return (
      <Input
        name={name}
        label={label}
        type={type}
        item={user}
        setItem={setUser}
        handleChange={handleChange}
        error={errors[name]}
      />
    );
  }

  return (
    <form onSubmit={e => handleSubmit(e)}>
      {renderInput("username", "Username")}
      {renderInput("password", "Password", "password")}
      {renderButton("Login")}
    </form>
  );
};

export default LoginForm;
