import React, { useState } from "react";
import Joi from "joi-browser";

import { register } from "../services/userServices";
import Form from "./common/form";

const RegisterForm = ({ history }) => {
  const [user, setUser] = useState({
    username: "",
    email: "",
    emailConfirmation: "",
    password: ""
  });
  const [errors, setErrors] = useState({});
  console.log("State errors", errors);
  const schema = {
    username: Joi.string().required(),
    email: Joi.string().required(),
    emailConfirmation: Joi.string().required(),
    password: Joi.string().required()
  };

  const inputList = [
    { name: "username", label: "Username", value: user.username },
    { name: "email", label: "Email", value: user.email },
    {
      name: "emailConfirmation",
      label: "Email confirmation",
      value: user.emailConfirmation
    },
    { name: "password", label: "Password", value: user.password }
  ];

  async function doSubmit() {
    await register(user);
    history.push("/login");
  }

  return (
    <Form
      inputList={inputList}
      data={user}
      setData={setUser}
      errors={errors}
      setErrors={setErrors}
      schema={schema}
      doSubmit={doSubmit}
    />
  );
};

export default RegisterForm;
