export function renderInput(
  name,
  label,
  data,
  setData,
  errors,
  setErrors,
  schema,
  type = "text"
) {
  return (
    <Input
      name={name}
      label={label}
      type={type}
      item={data}
      setItem={setData}
      handleChange={e =>
        handleChange(e, errors, setErrors, data, setData, schema)
      }
      error={errors[name]}
    />
  );
}
