import React from "react";

const Select = ({ name, label, options, item, setItem, handleChange }) => {
  return (
    <div className="form-group">
      <label htmlFor={`input-${name}`}>{label}</label>
      <select
        id={`input-${name}`}
        name={name}
        className="form-control"
        onChange={e => handleChange(e)} //{e => setItem({ ...item, [name]: e.currentTarget.value })}
        value={item[name]}
      >
        <option value="">-- Select one --</option>
        {options.map(option => (
          <option value={option.name} key={option.id}>
            {option.name}
          </option>
        ))}
      </select>
    </div>
  );
};

export default Select;
