import React, { useEffect, useState } from "react";
import Joi from "joi-browser";

import { getBook, updateBook, saveBook } from "../services/booksService";
import { getGenres } from "../services/genresService";
import Input from "./common/input";
import Select from "./common/select";

const BookForm = ({ history, match: item }) => {
  const [book, setBook] = useState({
    title: "",
    author_first_name: "",
    author_second_name: "",
    rate: "",
    genre: ""
  });
  const [genres, setGenres] = useState([]);
  const [errors, setErrors] = useState({});
  const urlId = item.params.id;
  const isNew = urlId === "new" ? true : false;

  useEffect(() => {
    let shouldIgnore = false;

    async function fetchGenres() {
      const genres = await getGenres();
      if (!shouldIgnore) setGenres(genres);
    }

    async function fetchBook() {
      const book = await getBook(urlId);
      if (!shouldIgnore) setBook(mapToState(book));
    }
    fetchGenres();
    if (!isNew) fetchBook();
    return () => {
      shouldIgnore = true;
    };
  }, [item, isNew, urlId]);

  function mapToState(book) {
    return {
      id: book.id,
      title: book.title,
      author_first_name: book.author_first_name,
      author_second_name: book.author_second_name,
      rate: book.rate,
      genre: book.genre
    };
  }

  const schema = {
    id: Joi.number(),
    title: Joi.string().required(),
    author_first_name: Joi.string().allow(""),
    author_second_name: Joi.string().allow(""),
    rate: Joi.number()
      .min(1)
      .max(10)
      .required(),
    genre: Joi.string().required()
  };

  function mapToFetchApi() {
    const genreId = genres
      .filter(genre => genre.name === book.genre)
      .map(genre => genre.id)[0];
    return { ...book, genre: genreId };
  }

  function validate() {
    const options = { abortEarly: false };
    const { error } = Joi.validate(book, schema, options);
    if (!error) return null;

    const errors = {};
    for (let item of error.details) errors[item.path[0]] = item.message;
    return errors;
  }

  function validateProperty({ name, value }) {
    const obj = { [name]: value };
    const propertySchema = { [name]: schema[name] };
    const { error } = Joi.validate(obj, propertySchema);

    return error ? error.details[0].message : null;
  }

  function handleChange({ currentTarget: input }) {
    const allErrors = errors;
    const errorMessage = validateProperty(input);
    if (errorMessage) allErrors[input.name] = errorMessage;
    else delete allErrors[input.name];

    setErrors(allErrors);
    setBook({ ...book, [input.name]: input.value });
  }

  function handleSubmit(e) {
    e.preventDefault();

    const errors = validate();
    setErrors(errors || {});
    if (errors) return;

    doSubmit();
  }

  function doSubmit() {
    const mappedBook = mapToFetchApi();
    if (!isNew) updateBook(book.id, mappedBook);
    else saveBook(mappedBook);
    console.log(history);
  }

  function renderInput(name, label, type = "text") {
    return (
      <Input
        name={name}
        label={label}
        type={type}
        item={book}
        setItem={setBook}
        handleChange={handleChange}
        error={errors[name]}
      />
    );
  }

  function renderSelect(name, label) {
    return (
      <Select
        name={name}
        label={label}
        options={genres}
        item={book}
        setItem={setBook}
        handleChange={handleChange}
        error={errors[name]}
      />
    );
  }

  function renderButton(label) {
    return (
      <button type="submit" className="btn btn-primary" disabled={validate()}>
        {label}
      </button>
    );
  }

  return (
    <form onSubmit={e => handleSubmit(e)}>
      {renderInput("title", "Title")}
      {renderInput("rate", "Rate")}
      {renderInput("author_first_name", "Name")}
      {renderInput("author_second_name", "Second name")}
      {renderSelect("genre", "Genre")}
      {renderButton("save")}
    </form>
  );
};

export default BookForm;
