import React from "react";

const TableHead = ({ columns, sortColumn, setSortColumn }) => {
  function handleSort(newPath) {
    const order =
      sortColumn.path !== newPath
        ? "asc"
        : sortColumn.order === "asc"
        ? "desc"
        : "asc";
    const path = newPath;
    setSortColumn({ path, order });
  }

  function renderSortIcon(column) {
    const style = <i class="fas fa-caret-"></i>;
    return sortColumn.path !== column.path
      ? ""
      : sortColumn.order === "asc"
      ? style + "down"
      : style + "up";
  }

  return (
    <thead>
      <tr>
        {columns.map((column, ind) => {
          return (
            <th key={ind} onClick={() => handleSort(column.path)}>
              {column.label}
              {}
            </th>
          );
        })}
      </tr>
    </thead>
  );
};

export default TableHead;
