import React from "react";

// Default props should be rewritten according to the needs.

const FilterMenu = ({
  items,
  currentItem,
  handleClick,
  ref = "name",
  id = "id"
}) => {
  return (
    <ul className="list-group clickable">
      {items.map(item => {
        console.log(
          "item.name",
          item.name,
          "currentItem.name",
          currentItem.name
        );
        const style = item.name === currentItem.name ? "active" : "";
        return (
          <li
            key={item[id] || item[ref]}
            className={"list-group-item" + style}
            onClick={
              item[id] ? () => handleClick(item[ref]) : () => handleClick("")
            }
          >
            {item[ref]}
          </li>
        );
      })}
    </ul>
  );
};

export default FilterMenu;
