import React, { useEffect, useState } from "react";
import { getBook } from "../services/booksService";

const BookForm = ({ match: item }) => {
  const [book, setBook] = useState({
    title: "",
    firstName: "",
    secondName: "",
    rate: "",
    genreName: ""
  });
  console.log("State book: ", book);

  useEffect(() => {
    let shouldIgnore = false;
    async function fetchBook() {
      const book = await getBook(item.params.id);
      if (!shouldIgnore) setBook(mapToState(book));
    }
    fetchBook();
    return () => {
      shouldIgnore = true;
    };
  }, [item]);

  function mapToState(book) {
    setBook({
      title: book.title,
      firstName: book.author_first_name,
      secondName: book.author_second_name,
      rate: book.rate,
      genre: book.genre
    });
  }

  return <h1>Book form</h1>;
};

export default BookForm;
