import React from "react";
import Joi from "joi-browser";

import Input from "./input";
// import Select from "./select";

const Form = ({ inputList, setData, errors, setErrors, schema }) => {
  const validate = () => {
    const options = { abortEarly: false };
    const { error } = Joi.validate(data, schema, options);
    if (!error) return null;

    const errors = {};
    for (let item of error.details) errors[item.path[0]] = item.message;
    return errors;
  };

  const validateProperty = ({ name, value }) => {
    const obj = { [name]: value };
    const propertySchema = { [name]: schema[name] };
    const { error } = Joi.validate(obj, propertySchema);

    return error ? error.details[0].message : null;
  };

  const handleChange = (
    { currentTarget: input },
    errors,
    data,
    setData,
    schema
  ) => {
    const allErrors = errors;
    const errorMessage = validateProperty(input, schema);
    if (errorMessage) allErrors[input.name] = errorMessage;
    else delete allErrors[input.name];

    setErrors(allErrors);
    setData({ ...data, [input.name]: input.value });
  };

  const handleSubmit = (e, setErrors, data, schema, doSubmit) => {
    e.preventDefault();

    const errors = validate(data, schema);
    setErrors(errors || {});
    if (errors) return;

    doSubmit();
  };

  const renderButton = (label, data, schema) => {
    return (
      <button
        type="submit"
        className="btn btn-primary"
        disabled={validate(data, schema)}
      >
        {label}
      </button>
    );
  };

  //    function renderSelect(name, label) {
  //     return (
  //       <Select
  //         name={name}
  //         label={label}
  //         options={genres}
  //         item={book}
  //         setItem={setBook}
  //         handleChange={handleChange}
  //         error={errors[name]}
  //       />
  //     );
  //   }

  const renderInput = (
    name,
    label,
    data,
    setData,
    errors,
    setErrors,
    schema,
    type = "text"
  ) => {
    return (
      <Input
        name={name}
        label={label}
        type={type}
        item={data}
        setItem={setData}
        handleChange={e =>
          handleChange(e, errors, setErrors, data, setData, schema)
        }
        error={errors[name]}
      />
    );
  };

  return (
    <form action="">
      {inputList.map(({ name, label, value, type = "text" }) => (
        <Input
          name={name}
          type={type}
          value={value}
          label={label}
          onChange={handleChange}
        />
      ))}
    </form>
  );
};

export default Form;
