import React, { useState } from "react";
import Joi from "joi-browser";

import { renderInput, handleSubmit } from "./common/form";

const RegisterForm = () => {
  const [user, setUser] = useState({
    username: "",
    email: "",
    password: "",
    passwordConfirmation: ""
  });
  const [errors, setErrors] = useState({});

  const schema = {
    username: Joi.string().required(),
    email: Joi.string().allow(""),
    password: Joi.string().required(),
    passwordConfirmation: Joi.ref("password")
  };

  return (
    <form onSubmit={e => handleSubmit(e, setErrors, user, schema)}>
      {renderInput(
        "username",
        "Username",
        user,
        setUser,
        errors,
        setErrors,
        schema
      )}
      {renderInput("email", "Email", user, setUser, errors, setErrors, schema)}
      {renderInput(
        "password",
        "Password",
        user,
        setUser,
        errors,
        setErrors,
        schema
      )}
      {renderInput(
        "passwordConfirmation",
        "Confirmation",
        user,
        setUser,
        errors,
        setErrors,
        schema
      )}
    </form>
  );
};

export default RegisterForm;
