import React from "react";

// DEFAULT PROPS
// SOLVE SHITTY ISSUE

const FilterMenu = ({ items, handleClick, ref = "ref", id = "id" }) => {
  return (
    <ul className="list-group clickable">
      {items.map(item => {
        return (
          <li
            key={item[id] || item[name]}
            className="list-group-item"
            onClick={
              item[id] ? () => handleClick(item[name]) : () => handleClick("")
            }
          >
            {item[name]}
          </li>
        );
      })}
    </ul>
  );
};

export default FilterMenu;
