import React, { useState } from "react";
import Joi from "joi-browser";

import { register } from "../services/userServices";
import Form from "./common/form";

const RegisterForm = ({ history }) => {
  const [user, setUser] = useState({
    username: "",
    email: "",
    emailConfirmation: "",
    password: ""
  });
  const [errors, setErrors] = useState({});

  const schema = {
    username: Joi.string().required(),
    email: Joi.string().required(),
    emailConfirmation: Joi.string().required(),
    password: Joi.string().required()
  };

  async function doSubmit() {
    await register(user);
    history.push("/login");
  }

  return (
    <form
      onSubmit={e => Form.handleSubmit(e, setErrors, user, schema, doSubmit)}
    >
      {Form.renderInput(
        "username",
        "Username",
        user,
        setUser,
        errors,
        setErrors,
        schema
      )}
      {Form.renderInput(
        "email",
        "Email",
        user,
        setUser,
        errors,
        setErrors,
        schema
      )}
      {Form.renderInput(
        "emailConfirmation",
        "Email Confirmation",
        user,
        setUser,
        errors,
        setErrors,
        schema
      )}
      {Form.renderInput(
        "password",
        "Password",
        user,
        setUser,
        errors,
        setErrors,
        schema,
        "password"
      )}
      {Form.renderButton("Login", user, schema)}
    </form>
  );
};

export default RegisterForm;
