import http from "./httpService";

const apiEndpoint = "users/";

export async function login({ username, password }) {
  const { data: token } = await http.post(apiEndpoint + "token/", {
    username,
    password
  }); // TRY (usename || email)
  localStorage.setItem("token", {
    access: token.access,
    refresh: token.refresh
  });
}
