import React, { useState } from "react";
import Joi from "joi-browser";

import { register } from "../services/userServices";
import { renderInput, renderButton, handleSubmit } from "./common/form";

const RegisterForm = ({ history }) => {
  const [theme, setTheme] = userState("checking context theme");
  const [user, setUser] = useState({
    username: "",
    email: "",
    emailConfirmation: "",
    password: ""
  });
  const [errors, setErrors] = useState({});

  const schema = {
    username: Joi.string().required(),
    email: Joi.string().required(),
    emailConfirmation: Joi.string().required(),
    password: Joi.string().required()
  };

  const ThemeContext = React.createContext("checking context");

  async function doSubmit() {
    await register(user);
    history.push("/login");
  }

  return (
    <ThemeContext.Provider value={theme}>
      <form onSubmit={e => handleSubmit(e, setErrors, user, schema, doSubmit)}>
        {renderInput(
          "username",
          "Username",
          user,
          setUser,
          errors,
          setErrors,
          schema
        )}
        {renderInput(
          "email",
          "Email",
          user,
          setUser,
          errors,
          setErrors,
          schema
        )}
        {renderInput(
          "emailConfirmation",
          "Email Confirmation",
          user,
          setUser,
          errors,
          setErrors,
          schema
        )}
        {renderInput(
          "password",
          "Password",
          user,
          setUser,
          errors,
          setErrors,
          schema,
          "password"
        )}
        {renderButton("Login", user, schema)}
      </form>
    </ThemeContext.Provider>
  );
};

export default RegisterForm;
