import React, { useEffect, useState } from "react";
import { getBook, saveBook } from "../services/booksService";
import { getGenres } from "../services/genresService";

const BookForm = ({ match: item }) => {
  const [book, setBook] = useState({
    title: "",
    firstName: "",
    secondName: "",
    rate: "",
    genre: ""
  });
  const [genres, setGenres] = useState([]);

  useEffect(() => {
    let shouldIgnore = false;

    async function fetchGenres() {
      const genres = await getGenres();
      if (!shouldIgnore) setGenres(genres);
    }

    const id = item.params.id;
    if (id === "new") return;

    async function fetchBook() {
      const book = await getBook(id);
      if (!shouldIgnore) setBook(mapToState(book));
    }
    fetchGenres();
    fetchBook();
    return () => {
      shouldIgnore = true;
    };
  }, [item]);

  function mapToState(book) {
    return {
      id: book.id,
      id_: book._id,
      title: book.title,
      firstName: book.author_first_name,
      secondName: book.author_second_name,
      rate: book.rate,
      genre: book.genre
    };
  }

  function mapForAPICall() {
    const genre = genres.filter(genre => genre.name === book.genre)[0];
    return { ...book, genre: genre.id };
  }

  function handleSubmit(e) {
    e.preventDefault();
    const book = mapForAPICall();
    console.log("book to be submitted", book);
    saveBook(book.id, book);
  }

  return (
    <form onSubmit={e => handleSubmit(e)}>
      <div className="form-group">
        <label htmlFor="input-title">Title</label>
        <input
          id="input-title"
          type="text"
          className="form-control"
          value={book.title}
          onChange={e => setBook({ ...book, title: e.currentTarget.value })}
        />
      </div>
      <div className="form-group">
        <label htmlFor="input-name">Author name</label>
        <input
          id="input-name"
          type="text"
          className="form-control"
          value={book.firstName}
          onChange={e => setBook({ ...book, firstName: e.currentTarget.value })}
        />
      </div>
      <button type="submit" className="btn btn-primary">
        Save
      </button>
    </form>
  );
};

export default BookForm;
