import React, { useEffect, useState } from "react";
import { getBook, updateBook, saveBook } from "../services/booksService";
import { getGenres } from "../services/genresService";

const BookForm = ({ match: item }) => {
  const [book, setBook] = useState({
    title: "",
    author_first_name: "",
    author_second_name: "",
    rate: "",
    genre: ""
  });
  const [genres, setGenres] = useState([]);
  const urlId = item.params.id;
  const isNew = urlId === "new" ? true : false;

  useEffect(() => {
    let shouldIgnore = false;

    async function fetchGenres() {
      const genres = await getGenres();
      if (!shouldIgnore) setGenres(genres);
    }

    if (isNew) return;

    async function fetchBook() {
      const book = await getBook(urlId);
      if (!shouldIgnore) setBook(mapToState(book));
    }
    fetchGenres();
    fetchBook();
    return () => {
      shouldIgnore = true;
    };
  }, [item, isNew, urlId]);

  function mapToState(book) {
    return {
      id: book.id,
      title: book.title,
      author_first_name: book.author_first_name,
      author_second_name: book.author_second_name,
      rate: book.rate,
      genre: book.genre
    };
  }

  function mapForAPICall() {
    const genre = genres.filter(genre => genre.name === book.genre)[0];
    return { ...book, genre: genre.id };
  }

  function handleSubmit(e) {
    e.preventDefault();
    const book = mapForAPICall();
    if (isNew) saveBook(book);
    else updateBook(book.id, book);
    console.log("book to be submitted", book);
  }

  function renderInput() {
    return;
    <div className="form-group">
      <label htmlFor={`input-${item}`}>{label}</label>
      <input
        id={`input-${item}`}
        type={type}
        className="form-control"
        value={book.title}
        onChange={e => setBook({ ...book, title: e.currentTarget.value })}
      />
    </div>;
  }

  return (
    <form onSubmit={e => handleSubmit(e)}>
      <div className="form-group">
        <label htmlFor="input-title">Title</label>
        <input
          id="input-title"
          type="text"
          className="form-control"
          value={book.title}
          onChange={e => setBook({ ...book, title: e.currentTarget.value })}
        />
      </div>
      <div className="form-group">
        <label htmlFor="input-name">Author name</label>
        <input
          id="input-name"
          type="text"
          className="form-control"
          value={book.author_first_name}
          onChange={e =>
            setBook({ ...book, author_first_name: e.currentTarget.value })
          }
        />
      </div>
      <div className="form-group">
        <label htmlFor="input-rate">Rate</label>
        <input
          id="input-rate"
          type="text"
          className="form-control"
          value={book.rate}
          onChange={e => setBook({ ...book, rate: e.currentTarget.value })}
        />
      </div>
      <button type="submit" className="btn btn-primary">
        Save
      </button>
    </form>
  );
};

export default BookForm;
