import React, { useState, useEffect } from "react";

import BooksTable from "./booksTable";
import FilterMenu from "./common/filterMenu";
import NewButton from "./common/newButton";
import Pagination from "./common/pagination";
import SearchBar from "./common/searchBar";

import { getBooks } from "../services/booksService";
import { getGenres } from "../services/genresService";
import { paginate } from "../utils/paginate";

const Books = () => {
  const [books, setBooks] = useState([]);
  const [currentGenre, setCurrentGenre] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [genres, setGenres] = useState([{ name: "check" }]);

  const pageSize = 2;
  const { booksToRender, totalCount } = getPagedData();

  console.log(genres);

  useEffect(() => {
    let isSubscribed = true;
    async function fetchData() {
      const fetchedGenres = await getGenres();
      if (isSubscribed) {
        setGenres(g => [g, { name: "All Genres" }, ...fetchedGenres]);
      }
      const books = await getBooks();
      if (isSubscribed) {
        setBooks(books);
      }
    }
    fetchData();
    return () => {
      isSubscribed = false;
    };
  }, []);

  function getFilteredBooks() {
    return currentGenre
      ? books.filter(book => book.genre === currentGenre)
      : books;
  }

  function getPagedData() {
    const filteredBooks = getFilteredBooks();
    return {
      booksToRender: paginate(filteredBooks, currentPage, pageSize),
      totalCount: filteredBooks.length
    };
  }

  return (
    <div className="row">
      <div className="col-2">
        <FilterMenu items={genres} handleClick={setCurrentGenre} />
      </div>
      <div className="col">
        <NewButton />
        <SearchBar />
        <BooksTable books={booksToRender} />
        <Pagination
          pageSize={pageSize}
          itemsAmount={totalCount}
          setCurrentPage={setCurrentPage}
        />
      </div>
    </div>
  );
};

export default Books;
