import React, { useEffect } from "react";
import { getBook } from "../services/booksService";

const BookForm = ({ match: item }) => {
  useEffect(async () => {
    const book = await getBook(item.params.id);
  }, []);

  console.log(book);

  return <h1>Book form</h1>;
};

export default BookForm;
