import React from "react";

// DEFAULT PROPS
// ALL GENRES

const FilterMenu = ({ items, handleClick }) => {
  return (
    <ul className="list-group clickable">
      {items.map(item => {

        return item.id? (
          <li
            key={item.id}
            className="list-group-item"
            onClick={() => handleClick(item.name)}
          >
            {item.name}
          </li>
        ) : "";
      })}
    </ul>
  );
};

export default FilterMenu;
